var jumpToCode = (function init() {
    // Classes of code we would like to highlight in the file view
    var missingCoverageClasses = ['.cbranch-no', '.cstat-no', '.fstat-no'];

    // Elements to highlight in the file listing view
    var fileListingElements = ['td.pct.low'];

    // We don't want to select elements that are direct descendants of another match
    var notSelector = ':not(' + missingCoverageClasses.join('):not(') + ') > '; // becomes `:not(a):not(b) > `

    // Selector that finds elements on the page to which we can jump
    var selector =
        fileListingElements.join(', ') +
        ', ' +
        notSelector +
        missingCoverageClasses.join(', ' + notSelector); // becomes `:not(a):not(b) > a, :not(a):not(b) > b`

    // The NodeList of matching elements
    var missingCoverageElements = document.querySelectorAll(selector);

    var currentIndex;

    function toggleClass(index) {
        missingCoverageElements
            .item(currentIndex)
            .classList.remove('highlighted');
        missingCoverageElements.item(index).classList.add('highlighted');
    }

    function makeCurrent(index) {
        toggleClass(index);
        currentIndex = index;
        missingCoverageElements.item(index).scrollIntoView({
            behavior: 'smooth',
            block: 'center',
            inline: 'center'
        });
    }

    function goToPrevious() {
        var nextIndex = 0;
        if (typeof currentIndex !== 'number' || currentIndex === 0) {
            nextIndex = missingCoverageElements.length - 1;
        } else if (missingCoverageElements.length > 1) {
            nextIndex = currentIndex - 1;
        }

        makeCurrent(nextIndex);
    }

    function goToNext() {
        var nextIndex = 0;

        if (
            typeof currentIndex === 'number' &&
            currentIndex < missingCoverageElements.length - 1
        ) {
            nextIndex = currentIndex + 1;
        }

        makeCurrent(nextIndex);
    }

    return function jump(event) {
        if (
            document.getElementById('fileSearch') === document.activeElement &&
            document.activeElement != null
        ) {
            // if we're currently focused on the search input, we don't want to navigate
            return;
        }

        switch (event.which) {
            case 78: // n
            case 74: // j
                goToNext();
                break;
            case 66: // b
            case 75: // k
            case 80: // p
                goToPrevious();
                break;
        }
    };
})();
window.addEventListener('keydown', jumpToCode);

/// <reference types="cypress" />

import admin from '../fixtures/admin.json';

const API_URL = '/api';

const limpiarCampos = () => {
  cy.request('GET', `${API_URL}/configuracion-campos?limit=100&page=1`).then((response) => {
    const items = response.body?.data?.items ?? [];
    items
      .filter((campo) => campo.nombre.startsWith('test_'))
      .forEach((campo) => {
        cy.request('DELETE', `${API_URL}/configuracion-campos/${campo.id}`);
      });
  });
};

const crearCampo = (payload) => {
  cy.request('POST', `${API_URL}/configuracion-campos`, payload).its('status').should('eq', 201);
};

const buildCampoPayload = (overrides = {}) => ({
  nombre: 'test_codigo_alfa',
  etiqueta: 'Cdigo Alfa',
  tipo: 'texto',
  aplica_a: 'productos',
  grupo: 'Pruebas automatizadas',
  orden: 99,
  obligatorio: false,
  visible_en_listado: true,
  visible_en_detalle: true,
  activo: true,
  ...overrides,
});

const ingresarComoAdmin = () => {
  cy.visit('/login');
  cy.get('input[type="email"]').clear().type(admin.email);
  cy.get('input[type="password"]').clear().type(admin.password, { log: false });
  cy.contains('button', 'Ingresar').click();
  cy.url().should('include', '/dashboard');
};

const irAConfiguracionCampos = () => {
  cy.contains('Campos dinmicos').click();
  cy.url().should('include', '/configuracion-campos');
};

const completarFormularioCampo = (campo) => {
  cy.get('input[placeholder="ej: fecha_vencimiento"]').clear().type(campo.nombre);
  cy.get('input[placeholder="ej: Fecha de vencimiento"]').clear().type(campo.etiqueta);
  cy.get('select').eq(0).select(campo.tipo);
  cy.get('select').eq(1).select(campo.aplica_a);
  cy.get('input[placeholder="ej: Informacin tcnica"]').clear().type(campo.grupo);
  cy.get('input[type="number"]').first().clear().type(String(campo.orden));
};

const verificarCampoEnListado = (etiqueta) => {
  cy.contains('td', etiqueta).should('be.visible');
};

const eliminarCampoDesdeUI = (etiqueta) => {
  cy.contains('tr', etiqueta).within(() => {
    cy.contains('button', 'Eliminar').click();
  });
  cy.contains('button', 'Eliminar', { matchCase: false }).filter(':visible').click();
  cy.contains('Campo eliminado correctamente', { timeout: 10000 }).should('be.visible');
};

describe('Configuracin de Campos - Flujo dinmico', () => {
  before(() => {
    cy.intercept('POST', `${API_URL}/auth/login`).as('login');
    cy.intercept('GET', `${API_URL}/configuracion-campos*`).as('getCampos');
    cy.intercept('POST', `${API_URL}/configuracion-campos`).as('createCampo');
    cy.intercept('PUT', `${API_URL}/configuracion-campos/*`).as('updateCampo');
    cy.intercept('DELETE', `${API_URL}/configuracion-campos/*`).as('deleteCampo');
    cy.intercept('POST', `${API_URL}/configuracion-campos/templates/aplicar`).as('applyTemplate');
  });

  beforeEach(() => {
    cy.window().then((win) => win.localStorage.clear());
  });

  afterEach(() => {
    limpiarCampos();
  });

  it('permite al admin crear, editar, listar y eliminar un campo dinmico', () => {
    ingresarComoAdmin();
    cy.wait('@login');
    irAConfiguracionCampos();
    cy.wait('@getCampos');

    cy.contains('button', 'Nuevo campo').click();

    const campoCreacion = buildCampoPayload({ nombre: 'test_codigo_beta', etiqueta: 'Cdigo Beta' });
    completarFormularioCampo(campoCreacion);
    cy.contains('button', 'Guardar').click();

    cy.wait('@createCampo').its('response.statusCode').should('eq', 201);
    cy.contains('Campo creado correctamente').should('be.visible');
    cy.wait('@getCampos');
    verificarCampoEnListado(campoCreacion.etiqueta);

    cy.contains('tr', campoCreacion.etiqueta).within(() => {
      cy.contains('button', 'Editar').click();
    });

    const campoEditado = { ...campoCreacion, etiqueta: 'Cdigo Beta Editado', orden: 88 };
    completarFormularioCampo(campoEditado);
    cy.contains('button', 'Guardar').click();
    cy.wait('@updateCampo').its('response.statusCode').should('eq', 200);
    cy.contains('Campo actualizado correctamente').should('be.visible');
    cy.wait('@getCampos');
    verificarCampoEnListado(campoEditado.etiqueta);

    eliminarCampoDesdeUI(campoEditado.etiqueta);
    cy.wait('@deleteCampo').its('response.statusCode').should('eq', 200);
    cy.wait('@getCampos');
    cy.contains('td', campoEditado.etiqueta).should('not.exist');
  });

  it('aplica un template existente y muestra campos asociados', () => {
    limpiarCampos();

    const templateCampo = buildCampoPayload({ nombre: 'test_template_fecha', etiqueta: 'Fecha de Instalacin', tipo: 'fecha', orden: 70 });
    crearCampo(templateCampo);

    ingresarComoAdmin();
    cy.wait('@login');
    irAConfiguracionCampos();
    cy.wait('@getCampos');

    limpiarCampos();
    crearCampo(templateCampo);

    cy.contains('Aplicar template').should('be.visible');
    cy.get('input[placeholder="Template"]').type('PLANTILLA_BASE');
    cy.contains('button', 'Aplicar').click();
    cy.wait('@applyTemplate');

    cy.wait('@getCampos');
    cy.contains('Campos dinmicos');
  });
});

import axios from 'axios';

const api = axios.create({
  baseURL: '/api',
});

api.interceptors.request.use((config) => {
  const token = localStorage.getItem('token');
  if (token) config.headers.Authorization = `Bearer ${token}`;
  return config;
});

// Manejo centralizado de respuestas no autorizadas
api.interceptors.response.use(
  (response) => response,
  (error) => {
    const status = error?.response?.status;
    if (status === 401 || status === 403) {
      try {
        localStorage.removeItem('token');
      } catch (_) {}
      if (window.location.pathname !== '/login') {
        window.location.href = '/login';
      }
    }
    return Promise.reject(error);
  }
);

export default api;

export default function Table({ columns, data, keyField = 'id', tableClassName = 'table table-hover align-middle', rowClassName, rowProps }) {
  return (
    <table className={tableClassName}>
      <thead>
        <tr>
          {columns.map((col) => (
            <th
              key={col.key || col.accessor}
              style={col.style}
              className={col.headerClassName}
              scope="col"
            >
              {col.label}
            </th>
          ))}
        </tr>
      </thead>
      <tbody>
        {data.map((row, index) => {
          const rowKey = colKey(row, keyField, index);
          const extraProps = resolveRowProps(rowProps, row, index);
          const mergedClassName = mergeClassNames(
            typeof rowClassName === 'function' ? rowClassName(row, index) : rowClassName,
            extraProps.className
          );
          const { className, ...restProps } = extraProps;
          return (
            <tr key={rowKey} className={mergedClassName} {...restProps}>
              {columns.map((col) => (
                <td key={(col.key || col.accessor) ?? col.label} className={col.className} style={col.cellStyle}>
                  {typeof col.render === 'function' ? col.render(row, index) : resolveValue(row, col.accessor)}
                </td>
              ))}
            </tr>
          );
        })}
      </tbody>
    </table>
  );
}

function colKey(row, keyField, index) {
  if (typeof keyField === 'function') return keyField(row, index);
  if (row && typeof row === 'object' && keyField in row) return row[keyField];
  return index;
}

function resolveRowProps(rowProps, row, index) {
  if (!rowProps) return {};
  if (typeof rowProps === 'function') {
    const result = rowProps(row, index);
    return result && typeof result === 'object' ? result : {};
  }
  return rowProps && typeof rowProps === 'object' ? rowProps : {};
}

function mergeClassNames(...classes) {
  return classes
    .filter((cls) => typeof cls === 'string' && cls.trim().length > 0)
    .join(' ')
    .trim() || undefined;
}

function resolveValue(row, accessor) {
  if (!accessor) return null;
  if (typeof accessor === 'function') return accessor(row);
  return row?.[accessor];
}

import SearchBar from '../base/SearchBar.jsx';
import Button from '../ui/Button.jsx';

export default function CategoriaFilters({
  searchKey,
  search,
  onSearch,
  limit,
  onLimitChange,
  limitOptions = [10, 20, 50],
  onReset,
}) {
  return (
    <div className="categoria-filters mb-3">
      <div className="row g-2 align-items-center">
        <div className="col-12 col-md-6">
          <SearchBar
            defaultValue={search}
            placeholder="Buscar por nombre o descripcin"
            onSearch={onSearch}
            debounce={300}
          >
            {onReset && (
              <Button type="button" variant="outline-secondary" onClick={onReset}>
                Limpiar
              </Button>
            )}
          </SearchBar>
        </div>
        <div className="col-auto ms-auto">
          <select className="form-select" style={{ width: 90 }} value={limit} onChange={onLimitChange}>
            {limitOptions.map((option) => (
              <option key={option} value={option}>{option}</option>
            ))}
          </select>
        </div>
      </div>
    </div>
  );
}

import { useMemo } from 'react';
import { Link } from 'react-router-dom';
import Table from '../base/Table.jsx';
import EmptyState from '../EmptyState.jsx';
import LoadingSpinner from '../LoadingSpinner.jsx';
import Alert from '../ui/Alert.jsx';
import { formatBoolean, formatCurrency, formatDateTime, formatNumber } from '../../utils/formatters.js';

export default function ProductoList({
  items = [],
  total = 0,
  page = 1,
  totalPages = 1,
  limit,
  isLoading = false,
  isFetching = false,
  errorMessage = '',
  successMessage = '',
  deletingId = null,
  categoriaLookup = {},
  proveedorLookup = {},
  onRequestDelete,
  onPrevPage,
  onNextPage,
  onGoToPage,
  sortBy,
  sortDir,
  onSortChange,
  camposLista = [],
  camposListaError = '',
  camposListaLoading = false,
}) {
  const normalizeCampoOptions = (opciones = []) => {
    if (!Array.isArray(opciones)) return [];
    return opciones
      .map((opt) => {
        if (!opt || typeof opt !== 'object') return null;
        const value = opt.value ?? opt.codigo ?? opt.id;
        const label = opt.label ?? opt.nombre ?? value;
        if (value === undefined || label === undefined) return null;
        return { value: String(value), label: String(label) };
      })
      .filter(Boolean);
  };

  const campoOptionsMap = useMemo(() => {
    const map = {};
    camposLista.forEach((campo) => {
      map[campo.nombre] = normalizeCampoOptions(campo.opciones);
    });
    return map;
  }, [camposLista]);

  const renderSortLabel = (sortKey, label) => {
    if (!onSortChange) return label;
    const isActive = sortBy === sortKey;
    const icon = isActive ? (sortDir === 'asc' ? 'bi-arrow-up-short' : 'bi-arrow-down-short') : 'bi-arrow-down-up';
    return (
      <button
        type="button"
        className={`btn btn-link btn-sm p-0 d-inline-flex align-items-center gap-1 ${isActive ? 'text-primary' : 'text-secondary'}`}
        onClick={() => onSortChange(sortKey)}
        title={`Ordenar por ${label}`}
      >
        <span>{label}</span>
        <i className={`bi ${icon}`} />
      </button>
    );
  };

  const columns = useMemo(() => {
    const baseColumns = [
      {
        key: 'id',
        label: renderSortLabel('id', 'ID'),
        style: { width: 70 },
        render: (row) => `#${row.id}`,
      },
      {
        key: 'codigo',
        label: renderSortLabel('codigo', 'Cdigo'),
        accessor: 'codigo',
      },
      {
        key: 'nombre',
        label: renderSortLabel('nombre', 'Nombre'),
        accessor: 'nombre',
      },
      {
        key: 'categoria',
        label: 'Categora',
        className: 'd-none d-md-table-cell',
        render: (row) => categoriaLookup[row.categoria_id] ?? row.categoria_id ?? '-',
      },
      {
        key: 'proveedor',
        label: 'Proveedor',
        className: 'd-none d-md-table-cell',
        render: (row) => proveedorLookup[row.proveedor_id] ?? row.proveedor_id ?? '-',
      },
      {
        key: 'stock',
        label: 'Stock',
        className: 'text-end',
        render: (row) => formatNumber(row.stock_actual ?? 0),
      },
      {
        key: 'precio',
        label: renderSortLabel('precio', 'Precio'),
        className: 'text-end',
        render: (row) => formatCurrency(row.precio ?? 0),
      },
    ];

    const dynamicColumns = camposLista.map((campo) => {
      const columnClassName = ['numero', 'decimal'].includes(campo.tipo)
        ? 'text-end'
        : campo.tipo === 'boolean'
          ? 'text-center'
          : '';

      return {
        key: `campo-${campo.nombre}`,
        label: campo.etiqueta ?? campo.nombre,
        className: columnClassName,
        render: (row) => {
          const raw = row?.atributos_personalizados?.[campo.nombre];
          const isEmpty = raw === undefined || raw === null || (typeof raw === 'string' && raw.trim().length === 0) || (Array.isArray(raw) && raw.length === 0);
          if (isEmpty) return '';

          const opciones = campoOptionsMap[campo.nombre] ?? [];

          const resolveOptionLabel = (value) => {
            const match = opciones.find((opt) => opt.value === String(value));
            return match?.label ?? String(value);
          };

          switch (campo.tipo) {
            case 'numero':
            case 'decimal':
              return formatNumber(raw);
            case 'boolean': {
              const normalized = typeof raw === 'boolean'
                ? raw
                : ['true', '1', 'yes', 'si', 's', 'on'].includes(String(raw).trim().toLowerCase());
              return formatBoolean(normalized);
            }
            case 'select':
              return resolveOptionLabel(raw);
            case 'multi_select': {
              const values = Array.isArray(raw) ? raw : [raw];
              const labels = values.map((value) => resolveOptionLabel(value));
              return labels.join(', ');
            }
            case 'fecha': {
              const date = raw instanceof Date ? raw : new Date(raw);
              if (Number.isNaN(date.getTime())) return String(raw);
              return formatDateTime(date).split(' ')[0];
            }
            case 'email':
              return <a href={`mailto:${raw}`}>{raw}</a>;
            case 'telefono':
              return <a href={`tel:${raw}`}>{raw}</a>;
            case 'url':
              return (
                <a href={String(raw)} target="_blank" rel="noopener noreferrer">
                  {raw}
                </a>
              );
            case 'color': {
              const color = String(raw);
              return (
                <span className="d-inline-flex align-items-center gap-2">
                  <span className="rounded-circle border" style={{ width: 14, height: 14, backgroundColor: color }} />
                  <span>{color}</span>
                </span>
              );
            }
            case 'texto_largo':
            case 'texto':
            default:
              return typeof raw === 'string' ? raw : JSON.stringify(raw);
          }
        },
      };
    });

    const accionColumn = {
      key: 'acciones',
      label: 'Acciones',
      headerClassName: 'text-end',
      className: 'text-end',
      style: { width: 190 },
      render: (row) => (
        <div className="d-flex justify-content-end gap-2">
          <Link className="btn btn-outline-secondary btn-sm" title="Ver" to={`/productos/${row.id}`}>
            <i className="bi bi-eye" />
          </Link>
          <Link className="btn btn-outline-primary btn-sm" title="Editar" to={`/productos/${row.id}/editar`}>
            <i className="bi bi-pencil" />
          </Link>
          <button
            className="btn btn-outline-danger btn-sm"
            title="Eliminar"
            onClick={() => onRequestDelete?.(row)}
            disabled={deletingId === row.id}
          >
            {deletingId === row.id ? (
              <span className="spinner-border spinner-border-sm" role="status" aria-hidden="true"></span>
            ) : (
              <i className="bi bi-trash" />
            )}
          </button>
        </div>
      ),
    };

    return [...baseColumns, ...dynamicColumns, accionColumn];
  }, [camposLista, campoOptionsMap, categoriaLookup, proveedorLookup, deletingId, onRequestDelete, renderSortLabel]);

  const handleGoToPage = (nextPage) => {
    if (!nextPage || nextPage === page || nextPage < 1 || nextPage > totalPages) return;
    if (onGoToPage) {
      onGoToPage(nextPage);
      return;
    }
    if (nextPage < page) {
      onPrevPage?.();
    } else if (nextPage > page) {
      onNextPage?.();
    }
  };

  const pageNumbers = useMemo(() => {
    const maxButtons = 5;
    const pages = [];
    let start = Math.max(1, page - Math.floor(maxButtons / 2));
    let end = Math.min(totalPages, start + maxButtons - 1);
    start = Math.max(1, end - maxButtons + 1);
    for (let current = start; current <= end; current += 1) {
      pages.push(current);
    }
    return pages;
  }, [page, totalPages]);

  const hasLimit = typeof limit === 'number' && limit > 0;
  const rangeStart = hasLimit ? (page - 1) * limit + 1 : (items.length ? 1 : 0);
  const rangeEnd = hasLimit ? Math.min(rangeStart + limit - 1, total) : items.length;
  const canPrev = page > 1;
  const canNext = page < totalPages;

  return (
    <div className="producto-list">
      {errorMessage && <Alert variant="danger" className="py-2">{errorMessage}</Alert>}
      {successMessage && <Alert variant="success" className="py-2">{successMessage}</Alert>}
      {camposListaError && <Alert variant="warning" className="py-2">{camposListaError}</Alert>}

      {isLoading ? (
        <LoadingSpinner label="Cargando productos..." />
      ) : items.length === 0 ? (
        <EmptyState title="Sin productos" description="No se encontraron productos para los filtros aplicados." icon="bi-box" />
      ) : (
        <div className="table-responsive">
          <Table columns={columns} data={items} />
        </div>
      )}

      <div className="d-flex flex-wrap justify-content-between align-items-center gap-2 mt-3">
        <div className="text-secondary small">
          {isFetching ? 'Actualizando' : (
            total === 0
              ? 'Sin resultados'
              : `Mostrando ${rangeStart}${rangeEnd} de ${total}  Pgina ${page} / ${totalPages}`
          )}
          {hasLimit ? `  Lmite ${limit}` : null}
          {camposListaLoading ? '  Cargando columnas personalizadas' : null}
        </div>
        <nav aria-label="Paginacin de productos">
          <ul className="pagination pagination-sm mb-0">
            <li className={`page-item ${!canPrev ? 'disabled' : ''}`}>
              <button className="page-link" type="button" onClick={() => handleGoToPage(1)} aria-label="Primera">
                <span aria-hidden="true"></span>
              </button>
            </li>
            <li className={`page-item ${!canPrev ? 'disabled' : ''}`}>
              <button className="page-link" type="button" onClick={() => handleGoToPage(page - 1)} aria-label="Anterior">
                <span aria-hidden="true"></span>
              </button>
            </li>
            {pageNumbers.map((pageNumber) => (
              <li key={pageNumber} className={`page-item ${pageNumber === page ? 'active' : ''}`}>
                <button className="page-link" type="button" onClick={() => handleGoToPage(pageNumber)}>
                  {pageNumber}
                </button>
              </li>
            ))}
            <li className={`page-item ${!canNext ? 'disabled' : ''}`}>
              <button className="page-link" type="button" onClick={() => handleGoToPage(page + 1)} aria-label="Siguiente">
                <span aria-hidden="true"></span>
              </button>
            </li>
            <li className={`page-item ${!canNext ? 'disabled' : ''}`}>
              <button className="page-link" type="button" onClick={() => handleGoToPage(totalPages)} aria-label="ltima">
                <span aria-hidden="true"></span>
              </button>
            </li>
          </ul>
        </nav>
      </div>
    </div>
  );
}

import { useMemo } from 'react';
import Table from '../base/Table.jsx';
import EmptyState from '../EmptyState.jsx';
import LoadingSpinner from '../LoadingSpinner.jsx';
import Alert from '../ui/Alert.jsx';
import Button from '../ui/Button.jsx';

const ROW_BASE_CLASS = 'table-row-clickable';

export default function ProveedorList({
  items = [],
  total = 0,
  page = 1,
  totalPages = 1,
  limit,
  isLoading = false,
  isFetching = false,
  errorMessage = '',
  successMessage = '',
  selectedId = null,
  editingId = null,
  deletingId = null,
  onSelect,
  onEdit,
  onRequestDelete,
  onPrevPage,
  onNextPage,
}) {
  const columns = useMemo(
    () => [
      {
        key: 'id',
        label: 'ID',
        style: { width: 70 },
        render: (row) => `#${row.id}`,
      },
      {
        key: 'nombre',
        label: 'Nombre',
        accessor: 'nombre',
      },
      {
        key: 'email',
        label: 'Email',
        className: 'd-none d-md-table-cell',
        render: (row) => row.email || '-',
      },
      {
        key: 'telefono',
        label: 'Telfono',
        className: 'd-none d-md-table-cell',
        render: (row) => row.telefono || '-',
      },
      {
        key: 'direccion',
        label: 'Direccin',
        className: 'd-none d-lg-table-cell',
        render: (row) => row.direccion || '-',
      },
      {
        key: 'activo',
        label: 'Estado',
        style: { width: 120 },
        render: (row) => (
          <span className={`badge ${row.activo ? 'bg-success-subtle text-success fw-semibold' : 'bg-secondary-subtle text-secondary fw-semibold'}`}>
            {row.activo ? 'Activo' : 'Inactivo'}
          </span>
        ),
      },
      {
        key: 'acciones',
        label: 'Acciones',
        headerClassName: 'text-end',
        className: 'text-end',
        style: { width: 200 },
        render: (row) => (
          <div className="d-flex justify-content-end gap-2">
            <Button
              variant={row.id === editingId ? 'secondary' : 'outline-secondary'}
              size="sm"
              onClick={(event) => {
                event.stopPropagation();
                onEdit?.(row);
              }}
            >
              <i className="bi bi-pencil me-1" /> {row.id === editingId ? 'Editando' : 'Editar'}
            </Button>
            <Button
              variant="outline-danger"
              size="sm"
              onClick={(event) => {
                event.stopPropagation();
                onRequestDelete?.(row);
              }}
              disabled={deletingId === row.id}
            >
              {deletingId === row.id ? (
                <span className="spinner-border spinner-border-sm" role="status" aria-hidden="true"></span>
              ) : (
                <i className="bi bi-trash" />
              )}
            </Button>
          </div>
        ),
      },
    ],
    [selectedId, deletingId, onSelect, onRequestDelete]
  );

  return (
    <div className="proveedor-list">
      {errorMessage && (
        <Alert variant="danger" className="py-2">
          {errorMessage}
        </Alert>
      )}
      {successMessage && (
        <Alert variant="success" className="py-2">
          {successMessage}
        </Alert>
      )}

      {isLoading ? (
        <LoadingSpinner label="Cargando proveedores..." />
      ) : items.length === 0 ? (
        <EmptyState
          title="Sin proveedores"
          description="No se encontraron proveedores para los filtros aplicados."
          icon="bi-truck"
        />
      ) : (
        <div className="table-responsive">
          <Table
            columns={columns}
            data={items}
            rowClassName={(row) => {
              const classes = [ROW_BASE_CLASS];
              if (row.id === selectedId) classes.push('table-active');
              if (editingId && row.id === editingId && row.id !== selectedId) classes.push('table-warning');
              if (!row.activo) classes.push('table-light');
              return classes.join(' ');
            }}
            rowProps={(row) => ({
              onClick: () => onSelect?.(row),
              style: { cursor: 'pointer' },
            })}
          />
        </div>
      )}

      <div className="d-flex justify-content-between align-items-center mt-2">
        <div className="text-secondary small">
          {isFetching ? 'Actualizando' : `Mostrando ${items.length} de ${total}  Pgina ${page} / ${totalPages}`}
          {typeof limit !== 'undefined' ? `  Lmite ${limit}` : null}
        </div>
        <div className="btn-group">
          <Button variant="outline-secondary" size="sm" onClick={onPrevPage} disabled={page <= 1}>
            <i className="bi bi-chevron-left" /> Prev
          </Button>
          <Button variant="outline-secondary" size="sm" onClick={onNextPage} disabled={page >= totalPages}>
            Next <i className="bi bi-chevron-right" />
          </Button>
        </div>
      </div>
    </div>
  );
}

import { createContext, useContext, useEffect, useMemo, useState } from 'react';
import api from '../api/api';

const AuthContext = createContext(null);

export function AuthProvider({ children }) {
  const [token, setToken] = useState(() => localStorage.getItem('token'));
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchMe() {
      if (!token) {
        setLoading(false);
        return;
      }
      try {
        const { data } = await api.get('/auth/me');
        setUser(data.data);
      } catch (e) {
        localStorage.removeItem('token');
        setToken(null);
        setUser(null);
      } finally {
        setLoading(false);
      }
    }
    fetchMe();
  }, [token]);

  const login = async (email, password) => {
    const { data } = await api.post('/auth/login', { email, password });
    const t = data.data.token;
    localStorage.setItem('token', t);
    setToken(t);
    setUser(data.data.user);
    return data;
  };

  const logout = () => {
    localStorage.removeItem('token');
    setToken(null);
    setUser(null);
  };

  const value = useMemo(() => ({ token, user, login, logout, loading, isAuthenticated: !!token }), [token, user, loading]);

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
}

export function useAuth() {
  const ctx = useContext(AuthContext);
  if (!ctx) throw new Error('useAuth must be used within AuthProvider');
  return ctx;
}

import { createContext, useCallback, useContext, useMemo, useRef, useState } from 'react';

const NotificationContext = createContext(null);

export function NotificationProvider({ children, autoHideDuration = 4000 }) {
  const [notifications, setNotifications] = useState([]);
  const counterRef = useRef(0);

  const removeNotification = useCallback((id) => {
    setNotifications((prev) => prev.filter((item) => item.id !== id));
  }, []);

  const addNotification = useCallback(
    (input) => {
      const normalized = typeof input === 'string'
        ? { message: input, variant: 'success', autoHide: true }
        : {
            message: input?.message,
            variant: input?.variant ?? input?.type ?? 'success',
            autoHide: input?.autoHide ?? true,
          };

      if (!normalized.message) return;

      const id = ++counterRef.current;
      setNotifications((prev) => [...prev, { id, message: normalized.message, variant: normalized.variant }]);
      if (normalized.autoHide) {
        setTimeout(() => removeNotification(id), autoHideDuration);
      }
    },
    [autoHideDuration, removeNotification]
  );

  const value = useMemo(() => ({ notifications, addNotification, removeNotification }), [notifications, addNotification, removeNotification]);

  return (
    <NotificationContext.Provider value={value}>
      {children}
    </NotificationContext.Provider>
  );
}

export function useNotifications() {
  const ctx = useContext(NotificationContext);
  if (!ctx) throw new Error('useNotifications must be used within NotificationProvider');
  return ctx;
}

import { useMutation, useQueryClient } from '@tanstack/react-query';
import api from '../api/api';

export default function useApiMutation({
  mutationFn,
  method = 'post',
  url,
  invalidate = [],
  onSuccess,
  onError,
  axiosConfig,
  queryClient: externalQueryClient,
} = {}) {
  if (!mutationFn && !url) {
    throw new Error('useApiMutation requires either mutationFn or url');
  }

  const queryClient = externalQueryClient || useQueryClient();

  const finalMutationFn = mutationFn || (async (payload) => {
    const response = await api.request({ method, url, data: payload, ...axiosConfig });
    return response.data?.data ?? response.data;
  });

  return useMutation({
    mutationFn: finalMutationFn,
    onSuccess: async (data, variables, context) => {
      await Promise.all(
        invalidate.map((key) => queryClient.invalidateQueries({ queryKey: Array.isArray(key) ? key : [key] }))
      );
      if (onSuccess) await onSuccess(data, variables, context, queryClient);
    },
    onError,
  });
}

import { useMemo } from 'react';
import { useQuery } from '@tanstack/react-query';
import { fetchAuditorias, fetchAuditoriaStats, fetchHistorialByRegistro } from '../services/auditoriaService';

